import React, {useEffect, useState} from 'react';
import { useDispatch, useSelector } from "react-redux";
import { Link, useHistory } from 'react-router-dom';
import { postPokemon, getAllTypes } from '../../redux/actions';

function validate(input){
    let error = {}

    if(!input.name){
        error.name = 'Debes asignar un nombre a tu pokemón'
    }
    if(!input.hp ){ 
        error.hp = 'Debes agregarle vida a tu pokemón'
    }
    else if(input.hp < 1){
        error.hp = 'La vida de tu pokemón debe ser mayor a 0'
    }
    if(!input.attack ){ 
        error.attack = 'Debes agregarle una fuerza de ataque a tu pokemón'
    }
    else if(input.attack < 1){
        error.attack = 'La fuerza de tu pokemón debe ser mayor a 0'
    }
    if(!input.defense ){ 
        error.defense = 'Debes agregarle defensa a tu pokemón'
    }
    else if(input.defense < 1){
        error.defense = 'La capacidad de defenderse de tu pokemón debe ser mayor a 0'
    }
    if(!input.speed ){ 
        error.speed = 'Debes agregarle velocidad a tu pokemón'
    }
    else if(input.speed < 1){
        error.speed = 'La velocidad de tu pokemón debe ser mayor a 0'
    }
    if(!input.height ){ 
        error.height = 'Debes agregarle altura a tu pokemón'
    }
    else if(input.height < 1){
        error.height = 'Tu pokemón no puede medir menos de un 1cm y más de 1mt'
    }
    if(!input.weight ){ 
        error.weight = 'Debes agregarle peso a tu pokemón'
    }
    else if(input.weight < 1){
        error.weight = 'Tu pokemón no puede pesar menos de 1kg'
    }
    if(input.image !== "" && !(/.jpg|.jpeg|.png/i).test(input.image)){ 
        error.image = 'Tu imagen debe tener una extensión jpg, jpeg o png.'
    }
    return error;
}

export default function PokemonCreate(){
    const dispatch = useDispatch()
    // const navigate = useNavigate() version react-router-dom 6
    const history = useHistory();
    const type = useSelector(state => state.type)
    const [submitButton, setSubmitButton] = useState(false)
    const [error, setError] = useState({
        name: "",
        image: "",
        hp: 0,
        attack: 0,
        defense: 0,
        speed: 0,
        height: 0, 
        weight: 0,
        types: []
    })
    const [input, setInput] = useState({
        name: "",
        image: "",
        hp: 0,
        attack: 0,
        defense: 0,
        speed: 0,
        height: 0, 
        weight: 0,
        types: []
    })

    const valid = (e) => {
        if(input[e] === ""){
            setError({
                ...error,
                [e] : `El campo ${e} no puede estar vacio.`
            })
            setSubmitButton(false)
        } else {
            setError({
                ...error,
                [e] : ""
            })
            setSubmitButton(true)
        }
    }

    useEffect(() => {
        dispatch(getAllTypes())
    }, []);

    const handleInputChange = (e) => {
        setInput({
            ...input,
            [e.target.name]: e.target.value
        })
        // setError(validate({
        //     ...input,
        //     [e.target.name]: e.target.value
        // }))
    }

    const handleSelect = (e) => {
        setInput({
            ...input,
            types: [...input.types, e.target.value]
        })
    }

    const handleDelete = (type) => {
        setInput({
            ...input,
            types: input.types.filter(t => t !== type)
        })
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        dispatch(postPokemon(input))
        alert("Tu Pokemón se ha creado con éxito")
        setInput({
            name: "",
            image: "",
            hp: 0,
            attack: 0,
            defense: 0,
            speed: 0,
            height: 0, 
            weight: 0,
            types: []
        })
        console.log(history)
      history.goBack()
    }

    return(
        <div>
            <Link to='/Home'><button>Home</button></Link>
            <h1>Crea tu Pokemón</h1>
            <form>
                <div>
                <label>Nombre:</label>
                    <input type='text' 
                    value={input.name} 
                     name='name'
                    onChange={(e) => handleInputChange(e)}/>
                    { error.name &&
                    (<p className='error'>{error.name}</p>)}
                </div>
                <div>
                <label>Vida:</label>
                    <input type='number' 
                    value={input.hp} 
                     name='hp'
                    onChange={(e) => handleInputChange(e)}
                    // onBlur={() => alert("te juiste de")}
                    />
                    { error.hp &&
                    (<p className='error'>{error.hp}</p>)}
                </div><div>
                <label>Ataque:</label>
                    <input type='number' 
                    value={input.attack} 
                     name='attack'
                    onChange={(e) => handleInputChange(e)}/>
                    { error.attack &&
                    (<p className='error'>{error.attack}</p>)}
                </div><div>
                <label>Defensa:</label>
                    <input type='number' 
                    value={input.defense} 
                     name='defense'
                    onChange={(e) => handleInputChange(e)}/>
                    { error.defense &&
                    (<p className='error'>{error.defense}</p>)}
                </div><div>
                <label>Velocidad:</label>
                    <input type='number' 
                    value={input.speed} 
                     name='speed'
                    onChange={(e) => handleInputChange(e)}/>
                    { error.speed &&
                    (<p className='error'>{error.speed}</p>)}
                </div><div>
                <label>Altura:</label>
                    <input type='number' 
                    value={input.height} 
                     name='height'
                    onChange={(e) => handleInputChange(e)}/>
                    { error.height &&
                    (<p className='error'>{error.height}</p>)}
                </div><div>
                <label>Peso:</label>
                    <input type='number' 
                    value={input.weight} 
                     name='weight'
                    onChange={(e) => handleInputChange(e)}/>
                    { error.weight &&
                    (<p className='error'>{error.weight}</p>)}
                </div><div>
                <label>Su imagen:</label>
                    <input type='url' 
                    value={input.image} 
                     name='image'
                    onChange={(e) => handleInputChange(e)}/>
                    { error.image &&
                    (<p className='error'>{error.image}</p>)}
                </div>
                <select onChange={(e) => handleSelect(e)}>
                {type?.map( t => ( 
                    <option value={t.name} key={t.id}>{ t.name }</option>
                ))}
            </select>
            {input.types.map( (ty, i) => <div className='typesCharacter' key={i}>
                <p>{ ty }</p>
                <button className='buttonDelete' onClick={() => handleDelete(ty)}>X</button>
                </div>
            )}
            <button type='submit' onClick={(e) => handleSubmit(e)}>Crear Pokemón</button>
            </form>
        </div>
    )
}

// [ ] Un formulario controlado con JavaScript con los campos mencionados en el detalle del Pokemon
// [ ] Posibilidad de seleccionar/agregar más de un tipo de Pokemon
// [ ] Botón/Opción para crear un nuevo Pokemon
