import React, { Fragment, useEffect, useState} from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from 'react-router-dom';
import { getAllPokemons } from "../../redux/actions";
import  Card from '../CardPokemon/Pokemon'

export default function Home(){

    const dispatch = useDispatch()
    const allPokemons = useSelector(state => state.pokemons)
    const [currentPage, setCurrentPage] = useState(1)
    const [pokemonsPerPage, setPokemonsPerPage] = useState(12)
    let lastPoke = currentPage * pokemonsPerPage  // 12
    let firstPoke = lastPoke - pokemonsPerPage // 0
    let currentPoke = allPokemons.slice(firstPoke, lastPoke)

    useEffect(() => {
        dispatch(getAllPokemons());
    }, [dispatch])

    return(
        <div>
            <Link to='/pokemons'>Pokemons</Link>
            <select>
                <option value= 'asc'>Ascendente</option>
                <option value= 'desc'>Descendente</option>
            </select>
            <select>
                <option value= 'type'>Tipo</option>
                <option value= 'exist'>Existente</option>
                <option value= 'create'>Creado</option>
            </select>
            <input></input>
            {
            allPokemons?.map( poke => {
               return( 
               <Fragment key={poke.id}>
                   <Link to={`/home/${poke.id}`}>
                   <Card name={poke.name} image={poke.image} type={poke.type} key={poke.id}/>
                   </Link>
               </Fragment>
               )
              }
            )
        }
        </div>

    )

}
