import axios from 'axios';

export function getAllPokemons(){
    return async function(dispatch){
        return fetch(`http://localhost:3001/pokemons`) // fecht me promete que me devuelve algo y lo convertir a jv
        .then(response => response.json())         //cuando lo convierto a obj lo despacho a una accion
        .then(data => dispatch({type: 'GET_ALL_POKEMONS', payload: data})
        )}
}

export function getAllTypes(){
    return async function(dispatch){
        return fetch(`http://localhost:3001/types`) 
        .then(response => response.json())         
        .then(data => dispatch({type: 'GET_ALL_TYPES', payload: data})
        )}
}

export function postPokemon(payload){
    return async function(){
        return fetch(`http://localhost:3001/pokemons`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json;charset=utf-8'
            },
            body: JSON.stringify(payload)
          })
        }
}
