import React from 'react';
import styles from './Paginated.module.css';
import { useState } from 'react';
import { useDispatch } from 'react-redux';

export default function Paginated({pokemonsPerPage, allPokemons, paginated}){
    const pageNumbers = []
    const [currentPage, setCurrentPage] = useState(1)
    const dispatch = useDispatch()

    const prevPage = (e) => {
        e.preventDefault();
        dispatch(setCurrentPage(currentPage - 1));
    };
    const nextPage = (e) => {
        e.preventDefault();
        dispatch(setCurrentPage(currentPage + 1));
      }

    for(let i = 0; i < Math.ceil(allPokemons/pokemonsPerPage); i++){
        pageNumbers.push( i + 1 )
    }

    return(
        <nav>
            <ul><li className={styles.paginated} key={0}>
                            <button className={styles.butPag}
                            onClick={(e) => prevPage(e)}> Prev </button></li>
                {
                    pageNumbers?.map( num => (
                        <li className={styles.paginated} key={num}>
                            <button className={styles.butPag}
                            onClick={() => paginated(num)}>{ num }</button>
                        </li>
                        )
                    )
                }
                <li className={styles.paginated} key={0}>
                            <button className={styles.butPag}
                            onClick={(e) => nextPage(e)}> Next </button></li>
            </ul>
        </nav>
    )
}
