export function getAllPokemons(){
    return async function(dispatch){
        return fetch(`http://localhost:3001/pokemons`) // fecht me promete que me devuelve algo y lo convertir a jv
        .then(response => response.json())         //cuando lo convierto a obj lo despacho a una accion
        .then(data => dispatch({type: 'GET_ALL_POKEMONS', payload: data})
        )}
}

export function getAllTypes(){
    return async function(dispatch){
        return fetch(`http://localhost:3001/types`) 
        .then(response => response.json())         
        .then(data => dispatch({type: 'GET_ALL_TYPES', payload: data})
        )}
}

export function postPokemon(){
    return async function(dispatch){
        return fetch(`http://localhost:3001/pokemons`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json;charset=utf-8'
            },
            body: JSON.stringify(user)
          })
        .then(response => response.json())         
        .then(data => dispatch({type: 'GET_ALL_TYPES', payload: data})
        )}
}

export function getPokemonByType(payload){
    return {
        type: 'GET_POKEMON_BY_TYPE',
        payload
    }
}

export function getPokemonDetail(id){
    return async function(dispatch){
    return fetch(`http://localhost:3001/pokemons/${id}`)
           .then(response => response.json())
            .then(data => dispatch({type: 'GET_POKEMON_DETAIL', payload: data})
        )}
}

export function filterByCreation(payload){
    return {
        type: 'FILTER_BY_CREATION',
        payload
    }
}

export function searchByName(name){ //el name es el payload con el cual buscamos al pokemon
    return async function(dispatch){
        return fetch(`http://localhost:3001/pokemons?name=${name}`) 
        .then(response => response.json())         
        .then(data => dispatch({type: 'SEARCH_BY_NAME', payload: data})
        )}
}

export function orderByName(payload){
    return {
        type: 'ORDER_BY_NAME',
        payload
    }
}

export function orderByStrength(payload){
    return {
        type: 'ORDER_BY_STRENGTH',
        payload
    }
}
