const initialState = {
  pokemons: [],
  allPokemons: [],
  pokemonDetail: {},
//   types: []
};

const rootReducer = (state = initialState, action) => {
    switch(action.type) {
        case 'GET_ALL_POKEMONS':
            return {
                ...state,
                pokemons: action.payload,
                allPokemons: action.payload
            };
        // case 'GET_ALL_TYPES':
        //     return {
        //         ...state,
        //         types: action.payload
        //     };
        case 'GET_POKEMON_BY_TYPE':
            let allPokemons = state.allPokemons
            let pokemonFiltered = action.payload === "all" ? allPokemons 
            : allPokemons.filter(poke => poke.type.includes(action.payload))
            return{
                ...state,
                pokemons: pokemonFiltered
            };
        case 'GET_POKEMON_DETAIL':
            return {
                ...state,
                pokemonDetail: action.payload
            };
        case 'FILTER_BY_CREATION':
            let poke = state.allPokemons
            let pokeFiltered = action.payload === "created" ? poke.filter(p => p.createdInDb) : poke.filter(p => !p.createdInDb)
            return {
                ...state,
                pokemons: action.payload === "all" ? poke : pokeFiltered
            };
            case 'ORDER_BY_NAME':
                let pokeName = state.allPokemons
                let pokeByName = action.payload === 'asc' ? pokeName.sort() : pokeName.sort().reverse()
            return {
                ...state,
                pokemon: pokeByName
            };
            case 'ORDER_BY_STRENGTH':
                // let pokeByStrength = 
            return {
                ...state,
                pokemonDetail: action.payload
            };
        default:
            return state;
    };
};

export default rootReducer;
