import React, {useEffect, useState} from 'react';
import { useDispatch, useSelector } from "react-redux";
import { Link, useHistory } from 'react-router-dom';
import { postPokemon, getAllTypes } from '../../redux/actions';



export default function PokemonCreate(){

    const dispatch = useDispatch()
    const types = useSelector(state => state.types)

    const [input, setInput] = useState({
        name: "",
        image: "",
        hp: 0,
        attack: 0,
        defense: 0,
        speed: 0,
        height: 0, 
        weight: 0,
        type: []
    })

    useEffect(() => {
        dispatch(getAllTypes())
    }, []);

    const handleInputChange = (e) => {
        setInput({
            ...input,
            [e.target.name]: e.target.value
        })
    }

    const handleSelect = (e) => {
        setInput({
            ...input,
            type: [...input.type, e.target.value]
        })
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        console.log(input)
        dispatch(postPokemon(input))
        alert("Tu Pokemón se ha creado con éxito")
    }

    return(
        <div>
            <Link to='/Home'><button>Home</button></Link>
            <h1>Crea tu Pokemón</h1>
            <form>
                <div>
                    <label>Nombre:</label>
                    <input type='text' 
                    value={input.name} 
                    placeholder='' name='name'
                    onChange={(e) => handleInputChange(e)}/>
                </div>
                <div>
                    <label>Vida:</label>
                    <input type='number' 
                    value={input.hp} 
                    placeholder='' name='hp'
                    onChange={(e) => handleInputChange(e)}/>
                </div><div>
                    <label>Ataque:</label>
                    <input type='number' 
                    value={input.attack} 
                    placeholder='' name='attack'
                    onChange={(e) => handleInputChange(e)}/>
                </div><div>
                    <label>Defensa:</label>
                    <input type='number' 
                    value={input.defense} 
                    placeholder='' name='defense'
                    onChange={(e) => handleInputChange(e)}/>
                </div><div>
                    <label>Peso:</label>
                    <input type='number' 
                    value={input.weight} 
                    placeholder='' name='weight'
                    onChange={(e) => handleInputChange(e)}/>
                </div><div>
                    <label>Velocidad:</label>
                    <input type='number' 
                    value={input.speed} 
                    placeholder='' name='speed'
                    onChange={(e) => handleInputChange(e)}/>
                </div><div>
                    <label>Su imagen:</label>
                    <input type='url' 
                    value={input.image} 
                    placeholder='' name='image'
                    onChange={(e) => handleInputChange(e)}/>
                </div>
                <select onChange={(e) => handleSelect(e)}>
                {types?.map( t => ( 
                    <option value={t.name} key={t.id} >{t.name}</option>
                ))}
            </select>
            <ul><li>{input.type.map(ty => `${ty} `)}</li></ul>
            <button type='submit'>Crear Pokemón</button>
            </form>
        </div>
    )
}

// [ ] Un formulario controlado con JavaScript con los campos mencionados en el detalle del Pokemon
// [ ] Posibilidad de seleccionar/agregar más de un tipo de Pokemon
// [ ] Botón/Opción para crear un nuevo Pokemon
