import React, {useEffect, useState} from 'react';
import { useDispatch, useSelector } from "react-redux";
import { Link, useHistory } from 'react-router-dom';
import { postPokemon, getAllTypes } from '../../redux/actions';



export default function PokemonCreate(){

    const dispatch = useDispatch()
    const types = useSelector(state => state.types)

    const [input, setInput] = useState({
        name: "",
        image: "",
        hp: 0,
        attack: 0,
        defense: 0,
        speed: 0,
        height: 0, 
        weight: 0,
        type: []
    })

    useEffect(() => {
        dispatch(getAllTypes())
    }, []);

    return(
        <div>
            <Link to='/Home'><button>Home</button></Link>
            <h1>Crea tu Pokem칩n</h1>
            <form>
                <div>
                    <label>Nombre:</label>
                    <input type='text' value={input.name} placeholder='' name='name'/>
                </div>
                <div>
                    <label>Vida:</label>
                    <input type='number' value={input.hp} placeholder='' name='hp'/>
                </div><div>
                    <label>Ataque:</label>
                    <input type='number' value={input.attack} placeholder='' name='attack'/>
                </div><div>
                    <label>Defensa:</label>
                    <input type='number' value={input.defense} placeholder='' name='defense'/>
                </div><div>
                    <label>Peso:</label>
                    <input type='number' value={input.weight} placeholder='' name='weight'/>
                </div><div>
                    <label>Velocidad:</label>
                    <input type='number' value={input.speed} placeholder='' name='speed'/>
                </div><div>
                    <label>Su imagen:</label>
                    <input type='text' value={input.image} placeholder='' name='image'/>
                </div>
                <label>Sus habilidades son:</label>
                    <input type='text' value={input.type} placeholder='' name='type'/>
            </form>
        </div>
    )
}

// [ ] Un formulario controlado con JavaScript con los campos mencionados en el detalle del Pokemon
// [ ] Posibilidad de seleccionar/agregar m치s de un tipo de Pokemon
// [ ] Bot칩n/Opci칩n para crear un nuevo Pokemon
