import React, {useEffect, useState} from 'react';
import { useDispatch, useSelector } from "react-redux";
import { Link, useHistory } from 'react-router-dom';
import { postPokemon, getAllTypes } from '../../redux/actions';

function validate(input){
    let error = {}

    if(!input.name){
        error.name = 'Debes asignar un nombre a tu pokemón'
    }
    if(!input.hp || input.hp < 1){
        error.hp = 'La vida de tu pokemón debe ser mayor a 0'
    }
    if(!input.attack || input.attack < 0){
        error.attack = 'mm'
    }
    // if(!input.attack || input.attack < 0){
    //     error.attack = ''
    // }
    // if(!input.attack || input.attack < 0){
    //     error.attack = ''
    // }
    // if(!input.attack || input.attack < 0){
    //     error.attack = ''
    // }
    // if(!input.attack || input.attack < 0){
    //     error.attack = ''
    // }
    return error;
}

export default function PokemonCreate(){
    const dispatch = useDispatch()
    // const navigate = useNavigate() version react-router-dom 6
    const history = useHistory();
    const type = useSelector(state => state.type)
    const [error, setError] = useState({})
    const [input, setInput] = useState({
        name: "",
        image: "",
        hp: 0,
        attack: 0,
        defense: 0,
        speed: 0,
        height: 0, 
        weight: 0,
        types: []
    })

    useEffect(() => {
        dispatch(getAllTypes())
    }, []);

    const handleInputChange = (e) => {
        setInput({
            ...input,
            [e.target.name]: e.target.value
        })
        setError(validate({
            ...input,
            [e.target.name]: e.target.value
        }))
    }

    const handleSelect = (e) => {
        setInput({
            ...input,
            types: [...input.types, e.target.value]
        })
    }

    const handleDelete = (type) => {
        setInput({
            ...input,
            types: input.types.filter(t => t !== type)
        })
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        dispatch(postPokemon(input))
        alert("Tu Pokemón se ha creado con éxito")
        setInput({
            name: "",
            image: "",
            hp: 0,
            attack: 0,
            defense: 0,
            speed: 0,
            height: 0, 
            weight: 0,
            types: []
        })
      history('/home')
    }

    return(
        <div>
            <Link to='/Home'><button>Home</button></Link>
            <h1>Crea tu Pokemón</h1>
            <form>
                <div>
                <label>Nombre:</label>
                    <input type='text' 
                    value={input.name} 
                     name='name'
                    onChange={(e) => handleInputChange(e)}/>
                    { error.name &&
                    (<p className='error'>{error.name}</p>)}
                </div>
                <div>
                <label>Vida:</label>
                    <input type='number' 
                    value={input.hp} 
                     name='hp'
                    onChange={(e) => handleInputChange(e)}/>
                    { error.hp &&
                    (<p className='error'>{error.hp}</p>)}
                </div><div>
                <label>Ataque:</label>
                    <input type='number' 
                    value={input.attack} 
                     name='attack'
                    onChange={(e) => handleInputChange(e)}/>
                    { error.attack &&
                    (<p className='error'>{error.attack}</p>)}
                </div><div>
                <label>Defensa:</label>
                    <input type='number' 
                    value={input.defense} 
                     name='defense'
                    onChange={(e) => handleInputChange(e)}/>
                </div><div>
                <label>Velocidad:</label>
                    <input type='number' 
                    value={input.speed} 
                     name='speed'
                    onChange={(e) => handleInputChange(e)}/>
                </div><div>
                <label>Altura:</label>
                    <input type='number' 
                    value={input.height} 
                     name='height'
                    onChange={(e) => handleInputChange(e)}/>
                </div><div>
                <label>Peso:</label>
                    <input type='number' 
                    value={input.weight} 
                     name='weight'
                    onChange={(e) => handleInputChange(e)}/>
                </div><div>
                <label>Su imagen:</label>
                    <input type='url' 
                    value={input.image} 
                     name='image'
                    onChange={(e) => handleInputChange(e)}/>
                </div>
                <select onChange={(e) => handleSelect(e)}>
                {type?.map( t => ( 
                    <option value={t.name} key={t.id} >{t.name}</option>
                ))}
            </select>
            {input.types.map( ty => <div className='typesCharacter'>
                <p>{ty}</p>
                <button className='buttonDelete' onClick={() => handleDelete(ty)}>X</button>
                </div>
            )}
            <button type='submit' onClick={(e) => handleSubmit(e)}>Crear Pokemón</button>
            </form>
        </div>
    )
}

// [ ] Un formulario controlado con JavaScript con los campos mencionados en el detalle del Pokemon
// [ ] Posibilidad de seleccionar/agregar más de un tipo de Pokemon
// [ ] Botón/Opción para crear un nuevo Pokemon
