import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import { useDispatch, useSelector } from "react-redux";
import { getPokemonDetail } from '../../redux/actions';


export default function PokemonDetail(props){
    console.log(props) // no me lee el props.match.params :(
    const dispatch = useDispatch()
    // tengo que ver la forma de diferenciar los poke de la base de datos de los de la api, de la bd tienen types
    // y los de la api tienen type desde su endpoint, el id es distinto tmb en ambos y habria que ver la action
    useEffect(() => {
        dispatch(getPokemonDetail(props.match.params.id))
    }, [dispatch])

    const myPokemon = useSelector( state => state.pokemonDetail )
console.log("POKEMON", myPokemon)
    return(
        <div>
            <h1>entrastes</h1>
            {
            myPokemon.length > 0 ? <div>
                <h1>{myPokemon[0].name.charAt(0).toUpperCase() + myPokemon[0].name.slice(1)}</h1>
                <img src={myPokemon[0].image} alt='Parece que a este pokemón no le gustan las fotos'/>
                {
                myPokemon[0].types?.map( ( t, i ) => (<p key={i}>{t}</p>))
                }
            </div>
            : <h1>Loading</h1>
        }
        <Link to='/home'><button>Volver</button></Link>
        </div>
    );
}


