import React, {useEffect, useState} from 'react';
import { useDispatch, useSelector } from "react-redux";
import { Link, useNavigate } from 'react-router-dom';
import { postPokemon, getAllTypes } from '../../redux/actions';



export default function PokemonCreate(){

    const dispatch = useDispatch()
    const navigate = useNavigate()
    const type = useSelector(state => state.type)

    const [input, setInput] = useState({
        name: "",
        image: "",
        hp: 0,
        attack: 0,
        defense: 0,
        speed: 0,
        height: 0, 
        weight: 0,
        types: []
    })

    useEffect(() => {
        dispatch(getAllTypes())
    }, []);

    const handleInputChange = (e) => {
        setInput({
            ...input,
            [e.target.name]: e.target.value
        })
    }

    const handleSelect = (e) => {
        setInput({
            ...input,
            types: [...input.types, e.target.value]
        })
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        dispatch(postPokemon(input))
        alert("Tu Pokemón se ha creado con éxito")
        setInput({
            name: "",
            image: "",
            hp: 0,
            attack: 0,
            defense: 0,
            speed: 0,
            height: 0, 
            weight: 0,
            types: []
        })
      navigate('/home')
    }

    return(
        <div>
            <Link to='/Home'><button>Home</button></Link>
            <h1>Crea tu Pokemón</h1>
            <form>
                <div>
                <label>Nombre:</label>
                    <input type='text' 
                    value={input.name} 
                    placeholder='' name='name'
                    onChange={(e) => handleInputChange(e)}/>
                </div>
                <div>
                <label>Vida:</label>
                    <input type="range" min="0" max="100" step='1'
                    value={input.hp} 
                    placeholder='' name='hp'
                    onChange={(e) => handleInputChange(e)}/>
                </div><div>
                <label>Ataque:</label>
                    <input type='number' 
                    value={input.attack} 
                    placeholder='' name='attack'
                    onChange={(e) => handleInputChange(e)}/>
                </div><div>
                <label>Defensa:</label>
                    <input type='number' 
                    value={input.defense} 
                    placeholder='' name='defense'
                    onChange={(e) => handleInputChange(e)}/>
                </div><div>
                <label>Velocidad:</label>
                    <input type='number' 
                    value={input.speed} 
                    placeholder='' name='speed'
                    onChange={(e) => handleInputChange(e)}/>
                </div><div>
                <label>Altura:</label>
                    <input type='number' 
                    value={input.height} 
                    placeholder='' name='height'
                    onChange={(e) => handleInputChange(e)}/>
                </div><div>
                <label>Peso:</label>
                    <input type='number' 
                    value={input.weight} 
                    placeholder='' name='weight'
                    onChange={(e) => handleInputChange(e)}/>
                </div><div>
                <label>Su imagen:</label>
                    <input type='url' 
                    value={input.image} 
                    placeholder='' name='image'
                    onChange={(e) => handleInputChange(e)}/>
                </div>
                <select onChange={(e) => handleSelect(e)}>
                {type?.map( t => ( 
                    <option value={t.name} key={t.id} >{t.name}</option>
                ))}
            </select>
            <ul><li>{input.types.map(ty => `${ty} `)}</li></ul>
            <button type='submit' onClick={(e) => handleSubmit(e)}>Crear Pokemón</button>
            </form>
        </div>
    )
}

// [ ] Un formulario controlado con JavaScript con los campos mencionados en el detalle del Pokemon
// [ ] Posibilidad de seleccionar/agregar más de un tipo de Pokemon
// [ ] Botón/Opción para crear un nuevo Pokemon
