export function getAllPokemons(){
    return async function(dispatch){
        return fetch(`http://localhost:3001/pokemons`) // fecht me promete que me devuelve algo y lo convertir a jv
        .then(response => response.json())         //cuando lo convierto a obj lo despacho a una accion
        .then(data => dispatch({type: 'GET_ALL_POKEMONS', payload: data})
        )}
}

