import React, { Fragment, useEffect, useState} from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from 'react-router-dom';
import { filterByCreation, getAllPokemons, getPokemonByType, orderByName } from "../../redux/actions";
import  Card from '../CardPokemon/Pokemon'
import Paginated from "./Paginated";
import SearchBar from "../Search/SearchBar";

export default function Home(){

    const dispatch = useDispatch()
    const allPokemons = useSelector(state => state.pokemons)
    const allTypes = useSelector(state => state.types)
    const [orden, setOrden] = useState("")
    const [currentPage, setCurrentPage] = useState(1)
    const [pokemonsPerPage] = useState(12)
    let lastPoke = currentPage * pokemonsPerPage  // 12
    let firstPoke = lastPoke - pokemonsPerPage // 0
    let currentPoke = allPokemons.slice(firstPoke, lastPoke)

     const paginated = (pageNumber) => {
         setCurrentPage(pageNumber)
     }

    useEffect(() => {
        dispatch(getAllPokemons());
    }, [dispatch])

    function handleFilterByType(e){
        dispatch(getPokemonByType(e.target.value))
    }
    function handleFilterByCreation(e){
        dispatch(filterByCreation(e.target.value))
    }
    function handleOrderByName(e){
        e.preventDefault()
        dispatch(orderByName(e.target.value));
        setCurrentPage(1)
        setOrden(`Ordenado ${e.target.value}`)
    }

    return(
        <div>
            <Link to='/pokemon'>Pokemon Create</Link>
            <select onChange={(e) => handleOrderByName(e)}>
                <option value= 'asc'>Ascendente</option>
                <option value= 'desc'>Descendente</option>
            </select>
            
            <select>
                {allTypes?.map( t => ( 
                    <option value={t.name} key={t.id}>{t.name}</option>
                ))}
            </select>
            
            {/* <select onChange={(e) => handleFilterByType(e)}>'
                <option value= 'all'>Tipo</option>
                <option value= 'normal'>Lo que se dice "Normal"</option>
                <option value= 'fighting'>Luchador</option>
                <option value= 'flying'>Volador</option>
                <option value= 'poison'>Venenoso</option>
                <option value= 'ground'>Tierra</option>
                <option value= 'rock'>Roca</option>
                <option value= 'bug'>Bicho</option>
                <option value= 'ghost'>Fantasma</option>
                <option value= 'steel'>Acero</option>
                <option value= 'fire'>Fuego</option>
                <option value= 'water'>Agua</option>
                <option value= 'grass'>Planta</option>
                <option value= 'electric'>Eléctrico</option>
                <option value= 'psychic'>Psíquico</option>
                <option value= 'ice'>Hielo</option>
                <option value= 'dragon'>Dragón</option>
                <option value= 'dark'>Siniestro</option>
                <option value= 'fairy'>Hada</option>
                <option value= 'unknown'>Se desconoce</option>
                <option value= 'shadow'>Sombra</option>
            </select> */}
            <select onChange={(e) => handleFilterByCreation(e)}>
                <option value= 'all'>Todos</option>
                <option value= 'exist'>Existentes</option>
                <option value= 'created'>Creados</option>
            </select>
            <SearchBar/>
            <Paginated
            pokemonsPerPage={ pokemonsPerPage }
            allPokemons={ allPokemons.length }
            paginated={ paginated }
            />
            
            {
            currentPoke?.map( poke => {
               return( 
               <Fragment key={poke.id}>
                   <Link to={`/home/${poke.id}`}>
                   <Card name={poke.name} image={poke.image ? poke.image : <img src='./imgDefault.png' alt='Sigue en su pokebola'/>} type={poke.type} key={poke.id}/>
                   </Link>
               </Fragment>
               )
              }
            )
        }
        </div>

    )

}
