import React, { Fragment, useEffect, useState} from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from 'react-router-dom';
import { filterByCreation, getAllPokemons, getAllTypes, getPokemonByType, order } from "../../redux/actions";
import  Card from '../CardPokemon/Pokemon'
import Paginated from "./Paginated";
import SearchBar from "../Search/SearchBar";
import style from './Home.module.css'
import styles from './Paginated'

export default function Home(){

    const dispatch = useDispatch()
    const allPokemons = useSelector(state => state.pokemons)
    const type = useSelector(state => state.type)
   
   //Paginación:
    const [orden, setOrden] = useState("")
    const [currentPage, setCurrentPage] = useState(1)
    const [pokemonsPerPage] = useState(12)
    let lastPoke = currentPage * pokemonsPerPage  // 12
    let firstPoke = lastPoke - pokemonsPerPage // 0
    let currentPoke = allPokemons.length > 2 ? allPokemons.slice(firstPoke, lastPoke) : allPokemons
    
    const prevPage = (e) => {
        e.preventDefault();
        if(currentPage > 1){
            setCurrentPage(currentPage - 1);
        }
       else{
           alert("No hay más páginas previas")
       }
    };
    const nextPage = (e) => {
        e.preventDefault();
       setCurrentPage(currentPage + 1);
      }

     const paginated = (pageNumber) => {
         setCurrentPage(pageNumber);
     }

    useEffect(() => {
        dispatch(getAllPokemons());
        dispatch(getAllTypes());
    }, [dispatch])

    function handleFilterByType(e){
        dispatch(getPokemonByType(e.target.value))
    }
    function handleFilterByCreation(e){
        dispatch(filterByCreation(e.target.value))
        
    }
    function handleOrder(e){
        e.preventDefault()
        dispatch(order(e.target.value));
        setCurrentPage(1)
        setOrden(`Ordenado ${e.target.value}`)
    }
    function handleRefresh(e){
        e.preventDefault()
        dispatch(getAllPokemons())
    }

    return(
        <div>
            <Link to='/'><button>Atrás</button></Link>
            <button onClick={(e) => {handleRefresh(e)}}>Volver a cargar los pokemón</button>
            <Link to='/pokemon'><button>Pokemon Create</button></Link>
            <select onChange={(e) => handleOrder(e)}>
            <option value='orden' key="">Ordenados</option>
                <option value= 'asc'>A-Z</option>
                <option value= 'desc'>Z-A</option>
                <option value= 'masFuerza'>+Fuerza</option>
                <option value= 'menosFuerza'>-Fuerza</option>
            </select>
            
            <select onChange={(e) => handleFilterByType(e)}>
                <option value="types" key="types">Por tipo</option>
                {type?.map( t => ( 
                    <option value={t.name} key={t.name} >{t.name.charAt(0).toUpperCase() + t.name.slice(1)}</option>))}
            </select>
            <select onChange={(e) => handleFilterByCreation(e)}>
                <option value= 'all'>Todos</option>
                <option value= 'exist'>Existentes</option>
                <option value= 'created'>Creados</option>
            </select>
            <SearchBar/>
            {
             currentPoke.length === 1 
             ? (<Fragment key={currentPoke[0].id}>
             <Link to={"/pokemons/"+currentPoke[0].id}>
             <Card name={currentPoke[0].name} image={currentPoke[0].image} types={currentPoke[0].types || currentPoke[0].type} key={currentPoke[0].id}/>
             </Link>
         </Fragment>)
            : currentPoke.length > 1
            ? currentPoke.map( poke => {
               return( 
               <Fragment key={poke.id}>
                   <div className={style}></div>
                   <Link to={"/pokemons/"+poke.id}>
                   <Card name={poke.name} image={poke.image} types={poke.types} key={poke.id}/>
                   </Link>
               </Fragment>
               )
              }
            )
            : <img src="https://cdn.dribbble.com/users/452800/screenshots/2458698/media/98b1eeba96d08837467810e3f253c301.gif" alt="Loading"/>    
        }
        <ul>
                <li className={styles.paginated}>
                    <button onClick={(e) => prevPage(e)}>Prev</button></li>
            <Paginated
            pokemonsPerPage={ pokemonsPerPage }
            allPokemons={ allPokemons.length }
            paginated={ paginated }
            />
            <li className={styles.paginated}>
                <button onClick={(e) => nextPage(e)}>Next</button></li>
            </ul>
        </div>
    )
}
